<template>    <div>        <p>{{askItem.name}}</p>        <ul>            <li class="left">                    <img :src="askItem.imgSrc" alt="">                    <span>{{askItem.text}}</span>            </li>            <li                    class="right"                    v-for="a in askItem.mytext"            >                <span>{{a}}</span>                <img :src="askItem.myimgSrc" alt="">            </li>        </ul>        <p>            <input type="text" v-model="page">            <button @click="noticeParent()">我的回复</button>        </p>    </div></template><script>export  default{   props:['askItem','noticeChild'],    data:function(){      return {          page:''      }    },    methods:{        noticeParent:function(item){            this.$emit('emitt',{                page : this.page            })        }    }}</script><style>    p{            text-align:center;            height:5%    }    ul{        height:515px;    }    li{         height:30px;         padding:3px 0;    }    .right{        text-align: right;    }    li img{        width: 30px;        height: 30px;    }    input{        width:200px;        margin:5px;    }</style>