<template>    <div>        <message-info :askItem="choose" :notice-child="noticeChild" @emitt="emitb"></message-info>    </div></template><script>var Message = require('../components/MessageInfo.vue');    export default{        data:function(){          return {              id:this.$route.query.id,              noticeChild : null            }        },       components:{           "messageInfo":Message       },        created:function(){            this.setMessage();        },       computed:{           getMessage : function(){               return this.$store.getters.getMessage           },           choose:function(){               for(var i=0;i<this.$store.getters.getMessage.length;i++)               {                   if(this.$store.getters.getMessage[i].id==this.id)                   {                       return this.$store.getters.getMessage[i]                   }               }           }       },        methods : {            setMessage : function(){                this.$http.get('src/static/messageData.json').then(function(res){                    this.$store.commit('setMessage',res.data);                },function(res){                    console.log(res.status)                })            },            emitb:function(opt){                this.noticeChild = opt.page;                this.choose.mytext.push(this.noticeChild)            }        }    }</script><style></style>